import { useState, useEffect } from "react";
import axios from "axios";
import moment from "moment";
import {
  Button,
  Card,
  CardContent,
  Grid,
  Stack,
  Typography,
} from "@mui/material";
import { Box } from "@mui/system";
import React from "react";
import Nav from "../Nav/Nav";

const Expense = () => {
  const [expenses, setExpenses] = useState([{}]);

  const getAllExpenses = async () => {
    console.log("I was ran");
    const expenses = await axios.get("http://localhost:3001/expenses");
    setExpenses(expenses.data);
    console.log(expenses);
    console.log(expenses.data);

    // return expenses;
  };

  useEffect(() => {
    getAllExpenses();
    console.log("This is useEffect");
    console.log(expenses);
  }, []);

  return (
    <>
      <Nav />
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          marginTop: "2rem",
          alignItems: "center",
          //   border: "2px solid blue",
        }}
      >
        <Typography variant="h2" color="secondary">
          Welcome to Expenses Screen
        </Typography>
      </Box>
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <Button
          variant="outlined"
          onClick={() => getAllExpenses()}
          sx={{ marginLeft: "2rem" }}
        >
          Get Expenses
        </Button>
        <Button
          href="/expenses/new"
          variant="contained"
          sx={{ marginLeft: "2rem" }}
        >
          New Expense
        </Button>
      </Box>

      <Box
        display="flex"
        flexDirection="column"
        justifyContent="center"
        alignItems="center"
      >
        {expenses.map((expense, pos) => (
          <Card
            sx={{
              width: 875,
              margin: "1rem",
              // border: "2px solid #1565c0",
              boxShadow: "0px 0px 5px rgba(0,0,0,0.3)",
            }}
            key={pos}
          >
            {console.log(expense)}
            <Stack spacing={6}>
              <CardContent
                sx={{
                  margin: "1rem",
                  textAlign: "center",
                }}
              >
                {/* <div> */}
                {/* <Stack
                display="flex"
                spacing={6}
                flexDirection="row"
                justifyContent="center"
                alignItems="center"
              > */}
                {/* <div> */}
                <Grid
                  container
                  sx={{
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                  }}
                >
                  <Grid item xs={12} sm={6} md={4}>
                    <Typography color="primary" variant="h5">
                      {expense.item}
                    </Typography>
                  </Grid>

                  <Grid item xs={12} sm={6} md={2}>
                    <Typography color="secondary" variant="h6">
                      {expense.amount}
                    </Typography>
                  </Grid>

                  <Grid item xs={12} sm={6} md={3}>
                    <Typography color="primary.light" variant="h6">
                      {moment.utc(String(expense.date)).format("DD MM YYY")}
                      {console.log(expense.date)}
                    </Typography>
                  </Grid>

                  <Grid item xs={12} sm={6} md={3}>
                    <Typography color="secondary.light" variant="h6">
                      {expense.category}
                    </Typography>
                  </Grid>
                  {/* ======================== */}
                  {/* <Typography color="primary" variant="h4">
                {expense.item}
              </Typography>
              <Typography color="secondary" variant="h6">
                {expense.amount}
              </Typography>
              <Typography color="primary.light" variant="h6">
                {expense.date}
              </Typography>
              <Typography color="secondary.light" variant="h6">
                {expense.category}
              </Typography> */}
                  {/* ======================= */}
                </Grid>
                {/* </div> */}
                {/* </Stack> */}
                {/* {expense.item} {expense.amount} */}
                {/* </div> */}
              </CardContent>
            </Stack>
          </Card>
        ))}
      </Box>
    </>
  );
};

export default Expense;
