                        moment.utc(String(expense.date)).format("DD MM YYYY")
                      )}
                      {console.log(expense.date)}
                    </Typography>
                  </Grid>

                  <Grid item xs={12} sm={6} md={3}>
                    <Typography color="secondary.light" variant="h6">
                      {expense.category}
                    </Typography>
                  </Grid> */}

        {/* Commenting Eeeeend */}

        <TableContainer
          component={Paper}
          sx={{ maxWidth: "1100px", marginTop: "0.5rem", marginBottom: "4rem" }}
        >
          <Table aria-label="Expenses">
            <TableHead>
              <TableRow sx={{ bgcolor: "primary.main", color: "#fff" }}>
                <TableCell sx={{ color: "#fff" }}>Item</TableCell>
                <TableCell sx={{ color: "#fff" }} align="left">
                  Amount
                </TableCell>
                <TableCell sx={{ color: "#fff" }} align="left">
                  Date
                </TableCell>
                <TableCell sx={{ color: "#fff" }}>Category</TableCell>
                <TableCell sx={{ color: "#fff" }}>Edit</TableCell>
                <TableCell sx={{ color: "#fff" }}>Delete</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {expenses.map((expense, pos) => (
                <TableRow
                  key={pos}
                  sx={{
                    "&:last-child td, &last-child th": { border: 0 },
                  }}
                >
                  <TableCell>
                    <Typography
                      color="primary"
                      variant="subtitle1"
                      sx={{ fontWeight: "400" }}
                    >
                      {expense.item}
                    </Typography>
                  </TableCell>
                  <TableCell align="left">
                    <Typography color="secondary" variant="subtitle2">
                      ${expense.amount}
                    </Typography>
                  </TableCell>
                  <TableCell align="left">
                    <Typography color="primary" variant="p">
                      {moment.utc(String(expense.date)).format("MM-DD-YYYY")}
                    </Typography>
                  </TableCell>
                  <TableCell>
                    <Typography color="secondary.light" variant="p">
                      {expense.category}
                    </Typography>
                  </TableCell>
                  <TableCell>
                    <IconButton
                      sx={{
                        "&:hover": {
                          color: "green",
                        },
                      }}
                      href={`expenses/${expense._id}`}
                    >
                      <EditIcon />
                    </IconButton>
                  </TableCell>
                  <TableCell>
                    <IconButton
                      sx={{
                        "&:hover": { color: "#ee0000" },
                      }}
                      onClick={() => {
                        deleteExpense(expense._id);
                      }}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>

        {/* ======================== */}
        {/* <Typography color="primary" variant="h4">
                {expense.item}
              </Typography>
              <Typography color="secondary" variant="h6">
                {expense.amount}
              </Typography>
              <Typography color="primary.light" variant="h6">
                {expense.date}
              </Typography>
              <Typography color="secondary.light" variant="h6">
                {expense.category}
              </Typography> */}
        {/* ======================= */}
        {/* </Grid> */}
        {/* </div> */}
        {/* </Stack> */}
        {/* {expense.item} {expense.amount} */}
